import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {
    AppBar,
    Toolbar,
    ListItem,
    IconButton,
    ListItemText,
    Avatar,
    Divider,
    List,
    Typography,
    Box,
    ListItemIcon
} from '@material-ui/core';
import {
    ArrowBack,
    AssignmentInd,
    Home,
    Apps,
    ContactMail
} from '@material-ui/icons';
import menina from '../portfolio-project-files/menina.jpg';

//CSS STYLES
const useStyles = makeStyles(theme => ({
    menuSliderContainer: {
        width:250,
        background: '#511',
        height: '30rem'

    },
    avatar: {
        display: 'block',
        margin: '0.5rem auto', //left auto
        width: theme.spacing(13),
        height: theme.spacing(13)

    },
    listItem:{
        color: 'tan'

    }

}));

const menuItems = [
   {
    listIcon: <Home/>,
    listText: 'Home'
   },
   {
    listIcon: <AssignmentInd/>,
    listText: 'Resume'
    },
    {
    listIcon: <Apps/>,
    listText: 'Portfolio'
    },
    {
    listIcon: <ContactMail/>,
    listText: 'Contacts'
    }
]

const Navbar = () => {
    const [state, setState] = useState({
        r
    })
     const classes = useStyles();

     const sideList = slider => {
        <Box className ={classes.menuSliderContainer} component= 'div'>
        <Avatar  className= {classes.avatar} src= {menina} alt='the man'/> 
        <Divider/>
       <List>
           {menuItems.map((listItem, key)=>(

           <ListItem button key= {key}>
               <ListItemIcon className= {classes.listItem}>{listItem.listIcon}</ListItemIcon>
               <ListItemText className= {classes.listItem} primary={listItem.listText}/>

           </ListItem>

            ))}

       </List>

    </Box>

     }



    return (
     < >
    <Box component= 'nav'>
        <AppBar position='static' style={{background: '#222'}}>
            <Toolbar>
                <IconButton>
                    <ArrowBack style={{ color: 'tomato' }}/>
                </IconButton>
                <Typography variant= 'h5' style={{color: 'tan'}}>Portfolio</Typography>
            </Toolbar>
        </AppBar>
    </Box>
    </>


    );
};

export default Navbar;
